package com.bxs.khabarnama.ui.fragments;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import android.widget.Toast;import com.bxs.khabarnama.R;import com.bxs.khabarnama.ui.activities.AllStationActivity;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;import butterknife.Unbinder;public class AllStationCategoryFragment extends Fragment {    public String TAG = AllStationCategoryFragment.class.getSimpleName();    @BindView(R.id.tv_name_letter1)    TextView mTvNameLetter1;    @BindView(R.id.tv_artist_name1)    TextView mTvArtistName1;    @BindView(R.id.tv_name_letter2)    TextView mTvNameLetter2;    @BindView(R.id.tv_artist_name2)    TextView mTvArtistName2;    @BindView(R.id.tv_name_letter3)    TextView mTvNameLetter3;    @BindView(R.id.tv_artist_name3)    TextView mTvArtistName3;    @BindView(R.id.tv_name_letter4)    TextView mTvNameLetter4;    @BindView(R.id.tv_artist_name4)    TextView mTvArtistName4;    @BindView(R.id.tv_name_letter5)    TextView mTvNameLetter5;    @BindView(R.id.tv_artist_name5)    TextView mTvArtistName5;    @BindView(R.id.tv_name_letter6)    TextView mTvNameLetter6;    @BindView(R.id.tv_artist_name6)    TextView mTvArtistName6;    Unbinder unbinder;    public static AllStationCategoryFragment getInstance() {        return new AllStationCategoryFragment();    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.fragment_all_station, container, false);        unbinder = ButterKnife.bind(this, view);        return view;    }    @Override    public void onViewCreated(View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);    }    @OnClick(R.id.tv_name_letter1)    protected void click1() {        Toast.makeText(getActivity(), "click one", Toast.LENGTH_SHORT).show();        startActivity(new Intent(getActivity(), AllStationActivity.class));    }    @Override    public void onDestroyView() {        super.onDestroyView();        unbinder.unbind();    }}